import { createStore } from 'redux';

const store = createStore(function(state,action) {
    if(action.type === 'add-todo') {
        
        return {
            ...state,
            todos: [
                ...state.todos,
                {
                    id: Math.random(),
                    name: action.payload.name,
                    completed: action.payload.completed
                }]
        }
    }

