import {  useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import '../Assets/styles/Form.css'

export default function Form() {

    const dispatch = useDispatch();

    const [todoName, setTodoName] = useState('');
    const [copyTodo, setCopyTodo] = useState(false);

    const todos = useSelector(function(state) {
        return state.todos
      }) 

    return (
        <div className="Form">
            <form onSubmit={(e) => {

                {
                    todos.map((todo) => {
                        todo.name === todoName ? setCopyTodo(true) : setCopyTodo(false)
                    })
                }
                
                e.preventDefault();
                {
                    todoName ? dispatch({
                        type: 'add-todo',
                        payload: {
                            id: Math.random(),
                            name: todoName,
                            completed: false,
                            copy: copyTodo
                        }
                    }) : e.preventDefault();
                }
                setTodoName('')
            }}>
                <input type="text" placeholder="Todo Name" value={todoName} onChange={(e) => {
                    setTodoName(e.target.value)
                }}/>
                <button className="btn addTodo" onClick={() => {
                }}>Add</button>
            </form>
        </div>
    );
}
