import { createStore } from 'redux';

const store = createStore(function(state,action) {
    if(action.type === 'add-todo') {
        
        return {
            ...state,
            totos: {
                    name: action.payload.title,
                    completed: action.payload.completed
                }
        }
    }

    return state;
}, {
    totos: 
        {
            name: "asd",
            completed: false
        }
    ,
    
});

export default store;
